  KEY `USERID` (`USERID`),
  CONSTRAINT `content_ibfk_1` FOREIGN KEY (`ARTICLEID`) REFERENCES `article` (`id`),
  CONSTRAINT `content_ibfk_2` FOREIGN KEY (`USERID`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;

